import React, { useState } from "react";
import { View, Text, TextInput, TouchableOpacity } from "react-native";
import styles from "./YourStyles"; // Import your merged styles

const SignInScreen = ({ navigation }) => {
  const [values, setValues] = useState({
    email: "",
    password: "",
  });

  const [loginStatus, setLoginStatus] = useState(null);

  const handleSignIn = () => {
    // Simulating API call with setTimeout
    // In reality, replace setTimeout with your axios request
    setTimeout(() => {
      // Mocking successful login
      if (
        values.email === "user@example.com" &&
        values.password === "password"
      ) {
        const token = "sampleToken"; // Replace this with the received token
        console.log("Login Successful");
        setLoginStatus("Login Successful");
        // Navigation logic in React Native (navigate to Home screen)
        navigation.navigate("Home"); // Example navigation action
      } else {
        console.error("Email or password is incorrect");
        setLoginStatus("Email or password is incorrect");
      }
    }, 1000); // Simulating delay for API call
  };

  return (
    <View style={styles.loginSignup}>
      <View style={styles.form}>
        <Text style={styles.formH2}>Sign In</Text>
        {loginStatus && (
          <Text style={styles.successMessage}>{loginStatus}</Text>
        )}
        <TextInput
          placeholder="Enter Email"
          value={values.email}
          onChangeText={(text) => setValues({ ...values, email: text })}
          style={styles.formInput}
        />
        <TextInput
          placeholder="Enter Password"
          secureTextEntry={true}
          value={values.password}
          onChangeText={(text) => setValues({ ...values, password: text })}
          style={styles.formInput}
        />
        <TouchableOpacity onPress={handleSignIn} style={styles.formButton}>
          <Text>Sign In</Text>
        </TouchableOpacity>
        <Text>If you do not have an account, click Sign Up button</Text>
        <TouchableOpacity
          onPress={() => navigation.navigate("Register")}
          style={styles.formButton}
        >
          <Text style={styles.formLink}>Sign Up</Text>
        </TouchableOpacity>
      </View>
    </View>
  );
};

export default SignInScreen;
