import React, { useState, useEffect } from "react";
import {
  StyleSheet,
  View,
  Text,
  TextInput,
  CheckBox,
  TouchableOpacity,
} from "react-native";
import { Avatar, Button } from "react-native-elements";
import { auth, firestore } from "../../firebase"; // Importing Firestore from firebase
import EditProfileScreen from "./EditProfileScreen";

export default function ProfileScreen({ navigation }) {
  const [updatedWeeklyData, setUpdatedWeeklyData] = useState([
    { day: "Monday", link: "", status: true },
    { day: "Tuesday", link: "", status: false },
    { day: "Wednesday", link: "", status: true },
    { day: "Thursday", link: "", status: false },
    { day: "Friday", link: "", status: true },
    { day: "Saturday", link: "", status: false },
    { day: "Sunday", link: "", status: true },
  ]);

  const handleSignOut = () => {
    auth
      .signOut()
      .then(() => {
        navigation.navigate("LoginScreen");
      })
      .catch((err) => alert(err.message));
  };

  const updateTable = () => {
    // Handle the update logic here
    console.log("Updated Weekly Data:", updatedWeeklyData);
  };

  const handleLinkChange = (index, link) => {
    const updatedData = [...updatedWeeklyData];
    updatedData[index].link = link;
    setUpdatedWeeklyData(updatedData);
  };

  const handleStatusChange = (index) => {
    const updatedData = [...updatedWeeklyData];
    updatedData[index].status = !updatedData[index].status;
    setUpdatedWeeklyData(updatedData);
  };

  useEffect(() => {
    // Fetch data from Firestore and update 'updatedWeeklyData'
    const fetchData = async () => {
      const weeklyWorkoutRef = firestore.collection("weeklyWorkout");
      const querySnapshot = await weeklyWorkoutRef.get();
      const data = querySnapshot.docs.map((doc) => ({
        day: doc.id, // Assuming 'day' is stored as document IDs
        link: doc.data().link,
        status: doc.data().status,
      }));
      setUpdatedWeeklyData(data);
    };
    fetchData();
  }, []); // Run only once on initial render

  return (
    <View style={styles.container}>
      {/* Rest of your JSX code */}
      {/* ... */}
    </View>
  );
}

const styles = StyleSheet.create({
  // Your style definitions
});
