import React, { useState, useEffect } from "react";
import {
  StyleSheet,
  View,
  Text,
  TouchableOpacity,
  FlatList,
  Picker,
  Modal,
  ScrollView,
} from "react-native";
import { Avatar, Button, SearchBar, Input } from "react-native-elements";
import { auth } from "../../firebase";

export default function DoctorProfile({ navigation }) {
  const handleSignOut = () => {
    auth
      .signOut()
      .then(() => {
        navigation.navigate("LoginScreen");
      })
      .catch((err) => alert(err.message));
  };

  const [userList, setUserList] = useState([
    { id: 1, name: 'User 1', medicalCondition: 'Condition 1', email: 'user1@example.com' },
    { id: 2, name: 'User 2', medicalCondition: 'Condition 2', email: 'user2@example.com' },
    // Add more users here
  ]);

  const [search, setSearch] = useState("");
  const [filteredUserList, setFilteredUserList] = useState(userList);

  const [isAddUserModalVisible, setAddUserModalVisible] = useState(false);
  const [newUser, setNewUser] = useState({ name: "", medicalCondition: "", email: "" });

  const openAddUserModal = () => {
    setAddUserModalVisible(true);
  };

  const closeAddUserModal = () => {
    setAddUserModalVisible(false);
  };

  const addUser = () => {
    if (newUser.name && newUser.medicalCondition && newUser.email) {
      setUserList([...userList, newUser]);
      closeAddUserModal();
    }
  };

  const [isCreateWorkoutModalVisible, setCreateWorkoutModalVisible] = useState(false);
  const [newWorkout, setNewWorkout] = useState({
    workoutName: "",
    date: "",
    exercises: [
      { name: "", sets: "", reps: "" },
      { name: "", sets: "", reps: "" },
      { name: "", sets: "", reps: "" },
      { name: "", sets: "", reps: "" },
      { name: "", sets: "", reps: "" },
    ],
  });

  const openCreateWorkoutModal = () => {
    setCreateWorkoutModalVisible(true);
  };

  const closeCreateWorkoutModal = () => {
    setCreateWorkoutModalVisible(false);
  };

  const createWorkout = () => {
    if (newWorkout.workoutName && newWorkout.date) {
      console.log("New Workout:", newWorkout);
      closeCreateWorkoutModal();
    }
  };

  useEffect(() => {
    searchFilterFunction(search);
  }, [search]);

  const navigateToUserProfile = (user) => {
    navigation.navigate("ProfileScreen", { user });
  };

  const searchFilterFunction = (text) => {
    setSearch(text);
    const newData = userList.filter((item) => {
      const itemEmail = item.email.toUpperCase();
      const searchText = text.toUpperCase();
      return itemEmail.includes(searchText);
    });
    setFilteredUserList(newData);
  };

  return (
    <View style={styles.container}>
      <View style={styles.header}>
        <Avatar
          rounded
          size={120}
          icon={{ name: "person", type: "material" }}
          avatarStyle={styles.avatarStyle}
          activeOpacity={0.7}
        />
        <Text style={styles.emailText}>{auth.currentUser?.email}</Text>
      </View>

      <View style={styles.buttonsContainer}>
        <Button
          type="solid"
          containerStyle={styles.buttonContainer}
          buttonStyle={styles.changePictureButton}
          titleStyle={styles.buttonTitle}
          title="Change Picture"
          onPress={() => console.log("Change this picture!")}
        />

        <Button
          type="solid"
          containerStyle={styles.buttonContainer}
          buttonStyle={styles.editProfileButton}
          titleStyle={styles.buttonTitle}
          title="Edit Profile"
          onPress={() => navigation.navigate("EditProfileScreen")}
        />

        <Button
          type="solid"
          containerStyle={styles.buttonContainer}
          buttonStyle={styles.addUserButton}
          titleStyle={styles.buttonTitle}
          title="Add User"
          onPress={openAddUserModal}
        />

        <Button
          type="solid"
          containerStyle={styles.buttonContainer}
          buttonStyle={styles.createWorkoutButton}
          titleStyle={styles.buttonTitle}
          title="Create Workout"
          onPress={openCreateWorkoutModal}
        />

        <Button
          type="solid"
          containerStyle={styles.buttonContainer}
          buttonStyle={styles.logOutButton}
          titleStyle={styles.buttonTitle}
          title="Log Out"
          onPress={handleSignOut}
        />
      </View>

      <SearchBar
        round
        searchIcon={{ size: 26 }}
        containerStyle={styles.searchContainer}
        inputContainerStyle={styles.searchInputContainer}
        placeholder="Search user by email"
        onChangeText={(text) => searchFilterFunction(text)}
        onClear={() => searchFilterFunction("")}
        value={search}
      />

      <Modal
        visible={isAddUserModalVisible}
        animationType="slide"
        transparent={true}
      >
        <View style={styles.modalContainer}>
          <View style={styles.formContainer}>
            <Text style={styles.formTitle}>Add User</Text>
            <Input
              placeholder="Name"
              onChangeText={(text) => setNewUser({ ...newUser, name: text })}
              value={newUser.name}
            />
            <Input
              placeholder="Medical Condition"
              onChangeText={(text) => setNewUser({ ...newUser, medicalCondition: text })}
              value={newUser.medicalCondition}
            />
            <Input
              placeholder="Email"
              onChangeText={(text) => setNewUser({ ...newUser, email: text })}
              value={newUser.email}
            />
            <Button title="Add User" onPress={addUser} />
            <Button title="Cancel" onPress={closeAddUserModal} />
          </View>
        </View>
      </Modal>

      <Modal
        visible={isCreateWorkoutModalVisible}
        animationType="slide"
        transparent={true}
      >
        <View style={styles.modalContainer}>
          <ScrollView contentContainerStyle={styles.scrollContainer}>
            <View style={styles.formContainer}>
              <Text style={styles.formTitle}>Create Workout</Text>
              <Input
                placeholder="Workout Name"
                onChangeText={(text) => setNewWorkout({ ...newWorkout, workoutName: text })}
                value={newWorkout.workoutName}
              />
              <Input
                placeholder="Date"
                onChangeText={(text) => setNewWorkout({ ...newWorkout, date: text })}
                value={newWorkout.date}
              />

              {newWorkout.exercises.map((exercise, index) => (
                <View key={index} style={styles.exerciseContainer}>
                  <Text style={styles.formSubtitle}>{`Exercise ${index + 1}`}</Text>
                  <Input
                    placeholder="Exercise Name"
                    onChangeText={(text) =>
                      setNewWorkout((prev) => {
                        const updatedExercises = [...prev.exercises];
                        updatedExercises[index].name = text;
                        return { ...prev, exercises: updatedExercises };
                      })
                    }
                    value={exercise.name}
                  />

                  <Text>Sets</Text>
                  <Picker
                    selectedValue={exercise.sets}
                    onValueChange={(value) =>
                      setNewWorkout((prevWorkout) => {
                        const updatedExercises = [...prevWorkout.exercises];
                        updatedExercises[index].sets = value;
                        return { ...prevWorkout, exercises: updatedExercises };
                      })
                    }
                  >
                    {Array.from({ length: 20 }, (_, i) => (
                      <Picker.Item key={i} label={(i + 1).toString()} value={i + 1} />
                    ))}
                  </Picker>

                  <Text>Reps</Text>
                  <Picker
                    selectedValue={exercise.reps}
                    onValueChange={(value) =>
                      setNewWorkout((prevWorkout) => {
                        const updatedExercises = [...prevWorkout.exercises];
                        updatedExercises[index].reps = value;
                        return { ...prevWorkout, exercises: updatedExercises };
                      })
                    }
                  >
                    {Array.from({ length: 20 }, (_, i) => (
                      <Picker.Item key={i} label={(i + 1).toString()} value={(i + 1).toString()} />
                    ))}
                  </Picker>
                </View>
              ))}

              <Button title="Add Exercise" onPress={() => {}} />

              <Button title="Create Workout" onPress={createWorkout} />
              <Button title="Cancel" onPress={closeCreateWorkoutModal} />
            </View>
          </ScrollView>
        </View>
      </Modal>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: "center",
    backgroundColor: "#f0f0f0",
    paddingTop: 20,
  },
  header: {
    alignItems: "center",
    marginBottom: 20,
  },
  avatarStyle: {
    borderWidth: 2,
    borderColor: "#fff",
    backgroundColor: "#3498db",
  },
  emailText: {
    fontSize: 18,
    fontWeight: "bold",
    marginTop: 10,
  },
  buttonsContainer: {
    flexDirection: "row",
    justifyContent: "space-around",
    marginBottom: 20,
    width: "80%",
  },
  buttonContainer: {
    flex: 1,
  },
  buttonTitle: {
    fontSize: 16,
  },
  changePictureButton: {
    backgroundColor: "#3498db",
    borderColor: "#3498db",
  },
  editProfileButton: {
    backgroundColor: "#27ae60",
    borderColor: "#27ae60",
  },
  logOutButton: {
    backgroundColor: "#e74c3c",
    borderColor: "#e74c3c",
  },
  searchContainer: {
    width: "80%",
    marginBottom: 10,
  },
  searchInputContainer: {
    backgroundColor: "#fff",
    borderBottomWidth: 0,
    borderTopWidth: 0,
  },
  tableContainer: {
    backgroundColor: "#fff",
    width: "80%",
    padding: 10,
  },
  tableRow: {
    flexDirection: "row",
    borderBottomWidth: 1,
    borderColor: "#ccc",
    alignItems: "center",
  },
  tableCell: {
    flex: 1,
    padding: 10,
    fontSize: 14,
  },
