import React, { useState } from "react";
import {
  StyleSheet,
  View,
  Text,
  TextInput,
  CheckBox,
  TouchableOpacity,
} from "react-native";
import { Avatar, Button } from "react-native-elements";
import { auth } from "../../firebase";
import { collection, addDoc, getFirestore } from "firebase/firestore";
import { doc, setDoc } from "firebase/firestore";

const firestore = getFirestore();

export default function ProfileScreen({ navigation }) {
  const handleSignOut = () => {
    auth
      .signOut()
      .then(() => {
        navigation.navigate("LoginScreen");
      })
      .catch((err) => alert(err.message));
  };

  const [updatedWeeklyData, setUpdatedWeeklyData] = useState([
    { day: "Monday", link: "", status: true },
    { day: "Tuesday", link: "", status: false },
    { day: "Wednesday", link: "", status: true },
    { day: "Thursday", link: "", status: false },
    { day: "Friday", link: "", status: true },
    { day: "Saturday", link: "", status: false },
    { day: "Sunday", link: "", status: true },
  ]);

  const updateTable = async () => {
    try {
      const updatePromises = updatedWeeklyData.map(async (rowData) => {
        const docId = rowData.day.toLowerCase();
        const dataToUpdate = {
          link: rowData.link,
          status: rowData.status,
        };
        await setDoc(doc(firestore, "weeklyWorkout", docId), dataToUpdate);
      });

      // Wait for all the updatePromises to resolve
      await Promise.all(updatePromises);

      console.log("Weekly data updated successfully!");
    } catch (error) {
      console.error("Error updating weekly data: ", error);
    }
  };

  const handleLinkChange = (index, link) => {
    const updatedData = [...updatedWeeklyData];
    updatedData[index].link = link;
    setUpdatedWeeklyData(updatedData);
  };

  const handleStatusChange = (index) => {
    const updatedData = [...updatedWeeklyData];
    updatedData[index].status = !updatedData[index].status;
    setUpdatedWeeklyData(updatedData);
  };

  return (
    <View style={styles.container}>
      {/* ... rest of your code ... */}

      <View style={styles.updateButtonContainer}>
        <TouchableOpacity style={styles.updateButton} onPress={updateTable}>
          <Text style={styles.updateButtonText}>Update</Text>
        </TouchableOpacity>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: "center",
    backgroundColor: "#f0f0f0",
    paddingTop: 20,
  },
  header: {
    alignItems: "center",
    marginBottom: 20,
  },
  avatarStyle: {
    borderWidth: 2,
    borderColor: "#fff",
    backgroundColor: "#3498db",
  },
  emailText: {
    fontSize: 18,
    fontWeight: "bold",
    marginTop: 10,
  },
  buttonsContainer: {
    flexDirection: "row",
    justifyContent: "space-around",
    marginBottom: 20,
    width: "80%",
  },
  buttonContainer: {
    flex: 1,
  },
  buttonTitle: {
    fontSize: 16,
  },
  changePictureButton: {
    backgroundColor: "#3498db",
    borderColor: "#3498db",
  },
  editProfileButton: {
    backgroundColor: "#27ae60",
    borderColor: "#27ae60",
  },
  logOutButton: {
    backgroundColor: "#e74c3c",
    borderColor: "#e74c3c",
  },
  tableContainer: {
    backgroundColor: "#fff",
    width: "80%",
    padding: 10,
  },
  tableRow: {
    flexDirection: "row",
    borderBottomWidth: 1,
    borderColor: "#ccc",
    alignItems: "center",
  },
  tableCell: {
    flex: 1,
    padding: 10,
    fontSize: 14,
  },
  tableHeader: {
    fontWeight: "bold",
    marginLeft: 120,
  },
  linkInputContainer: {
    flex: 2,
    borderColor: "#ccc",
    borderWidth: 1,
  },
  linkInput: {
    padding: 5,
  },
  checkboxContainer: {
    flex: 1,
    alignItems: "flex-start",
    marginLeft: 60,
  },
  updateButtonContainer: {
    flex: 1,
    alignItems: "center",
  },
  updateButton: {
    backgroundColor: "#3498db",
    padding: 5,
    borderRadius: 5,
    marginTop: 2,
  },
  updateButtonText: {
    color: "#fff",
    textAlign: "center",
  },
});
