import React, { useState, useEffect, TouchableOpacity } from "react";
import { StyleSheet, View, Text } from "react-native";
import { auth } from "../../firebase";
import { getFirestore, collection, getDocs } from "firebase/firestore";

const firestore = getFirestore();

export default function ProfileScreen({ navigation, route }) {
  const { successMessage } = route.params || {};
  const [workoutPlanList, setWorkoutPlanList] = useState([]);

  useEffect(() => {
    const fetchWorkoutPlans = async () => {
      try {
        const workoutPlanCollection = collection(firestore, 'workoutPlan');
        const snapshot = await getDocs(workoutPlanCollection);
        const workoutPlanData = snapshot.docs.map((doc) => {
          const workoutName = doc.data().workoutName;
          const workoutDate = doc.data().workoutDate;
          const status = doc.data().status;

          // Log the values if needed
          console.log("Workout Name:", workoutName);
          console.log("Workout Date:", workoutDate);
          console.log("Status:", status);

          return { id: doc.id, workoutName, workoutDate, status };
        });

        setWorkoutPlanList(workoutPlanData);
      } catch (error) {
        console.error("Error getting workout plan documents: ", error);
      }
    };

    fetchWorkoutPlans();
  }, []);

  const handleSignOut = () => {
    auth
      .signOut()
      .then(() => {
        navigation.navigate("LoginScreen");
      })
      .catch((err) => alert(err.message));
  };


  return (
    <View style={styles.container}>
      <View style={styles.header}>
        {/* Display user information, similar to your existing code */}
      </View>
      <Text>{successMessage}</Text>

      <View style={styles.tableContainer}>
        <View style={styles.tableRow}>
          <Text style={[styles.tableCell, styles.tableHeader]}>Workout Name</Text>
          <Text style={[styles.tableCell, styles.tableHeader]}>Workout Date</Text>
          <Text style={[styles.tableCell, styles.tableHeader]}>Status</Text>
          <Text style={[styles.tableCell, styles.tableHeader]}>Update</Text>
        </View>
        {workoutPlanList.map((rowData) => (
          <View style={styles.tableRow} key={rowData.id}>
            <Text style={styles.tableCell}>{rowData.workoutName}</Text>
            <Text style={styles.tableCell}>{rowData.workoutDate}</Text>
            <Text style={styles.tableCell}>{rowData.status}</Text>
            <TouchableOpacity style={styles.updateButton} onPress={() => console.log("Update")}>
              <Text style={styles.updateButtonText}>Update</Text>
            </TouchableOpacity>
          </View>
        ))}
      </View>
    </View>
  );
}
const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: "center",
    backgroundColor: "#f0f0f0",
    paddingTop: 20,
  },
  header: {
    alignItems: "center",
    marginBottom: 20,
  },
  avatarStyle: {
    borderWidth: 2,
    borderColor: "#fff",
    backgroundColor: "#3498db",
  },
  emailText: {
    fontSize: 18,
    fontWeight: "bold",
    marginTop: 10,
  },
  buttonsContainer: {
    flexDirection: "row",
    justifyContent: "space-around",
    marginBottom: 20,
    width: "80%",
  },
  buttonContainer: {
    flex: 1,
  },
  buttonTitle: {
    fontSize: 16,
  },
  changePictureButton: {
    backgroundColor: "#3498db",
    borderColor: "#3498db",
  },
  editProfileButton: {
    backgroundColor: "#27ae60",
    borderColor: "#27ae60",
  },
  logOutButton: {
    backgroundColor: "#e74c3c",
    borderColor: "#e74c3c",
  },
  tableContainer: {
    backgroundColor: "#fff",
    width: "80%",
    padding: 10,
  },
  tableRow: {
    flexDirection: "row",
    borderBottomWidth: 1,
    borderColor: "#ccc",
    alignItems: "center",
  },
  tableCell: {
    flex: 1,
    padding: 10,
    fontSize: 14,
  },
  tableHeader: {
    fontWeight: "bold",
    marginLeft: 120,
  },
  linkInputContainer: {
    flex: 2,
    borderColor: "#ccc",
    borderWidth: 1,
  },
  linkInput: {
    padding: 5,
  },
  checkboxContainer: {
    flex: 1,
    alignItems: "flex-start",
    marginLeft: 60,
  },
  updateButtonContainer: {
    flex: 1,
    alignItems: "center",
  },
  updateButton: {
    backgroundColor: "#3498db",
    padding: 5,
    borderRadius: 5,
    marginTop: 2,
  },
  updateButtonText: {
    color: "#fff",
    textAlign: "center",
  },
});
