import React, { useState, useEffect } from "react";
import {
  StyleSheet,
  View,
  Text,
  TouchableOpacity,
  FlatList,
  Picker,
  Modal,
  ScrollView,
} from "react-native";
import { Avatar, Button, SearchBar, Input } from "react-native-elements";
import { auth, firestore } from "../../firebase";

const initialExercises = [
  { name: "", sets: "", reps: "" },
  { name: "", sets: "", reps: "" },
  { name: "", sets: "", reps: "" },
  { name: "", sets: "", reps: "" },
];

export default function DoctorProfile({ navigation }) {
  const handleSignOut = () => {
    auth
      .signOut()
      .then(() => {
        navigation.navigate("LoginScreen");
      })
      .catch((err) => alert(err.message));
  };

  const [selectedPatientId, setSelectedPatientId] = useState("");
  const [patientList, setPatientList] = useState([]); // State to store patient data

  // State to store patient data

  useEffect(() => {
    // Fetch patient data from Firestore
    const fetchPatients = async () => {
      try {
        const patientsCollection = firestore.collection("patients");
        const snapshot = await patientsCollection.get();
        const patientsData = snapshot.docs.map((doc) => ({ id: doc.id, ...doc.data() }));
        setPatientList(patientsData);
      } catch (error) {
        console.error("Error getting patient documents: ", error);
      }
    };

    fetchPatients();
  }, []);



  const [search, setSearch] = useState("");
  const [filteredUserList, setFilteredUserList] = useState(userList);


  const [isCreateWorkoutModalVisible, setCreateWorkoutModalVisible] = useState(false);
  const [newWorkout, setNewWorkout] = useState({
    workoutName: "",
    date: "",
    exercises: [...initialExercises],
  });

  const openCreateWorkoutModal = () => {
    setCreateWorkoutModalVisible(true);
  };

  const closeCreateWorkoutModal = () => {
    setCreateWorkoutModalVisible(false);
  };

  const createWorkout = () => {
    if (newWorkout.workoutName && newWorkout.date) {
      console.log("New Workout:", newWorkout);
      closeCreateWorkoutModal();
    }
  };

  useEffect(() => {
    searchFilterFunction(search);
  }, [search]);

  const navigateToUserProfile = (user) => {
    navigation.navigate("ProfileScreen", { user });
  };

  const searchFilterFunction = (text) => {
    setSearch(text);
    const newData = userList.filter((item) => {
      const itemEmail = item.email.toUpperCase();
      const searchText = text.toUpperCase();
      return itemEmail.includes(searchText);
    });
    setFilteredUserList(newData);
  };

  return (
    <View style={styles.container}>
      <View style={styles.header}>
        <Avatar
          rounded
          size={120}
