import React, { useState, useEffect } from "react";
import { View, Text, TextInput, TouchableOpacity, StyleSheet, Picker } from "react-native";
import { collection, getDocs, addDoc, where, query, updateDoc, doc } from "firebase/firestore";
import { firestore, auth } from "../../firebase";

export default function EditProfileScreen({ navigation }) {
  const [values, setValues] = useState({
    firstName: "",
    lastName: "",
    birthday: "",
    phoneNumber: "",
    organization: "",
  });
  const [organizationList, setOrganizationList] = useState([]);
  const user = auth.currentUser;

  const colRefProfile = collection(firestore, 'therapist');
  const colRefOrganizations = collection(firestore, 'organizations'); // Assuming you have a collection for organizations

  useEffect(() => {
    // Fetch organizations and update organizationList state
    const fetchOrganizations = async () => {
      try {
        const organizationsSnapshot = await getDocs(colRefOrganizations);
        const organizationsData = organizationsSnapshot.docs.map(doc => ({
          id: doc.id,
          ...doc.data()
        }));
        setOrganizationList(organizationsData);
      } catch (error) {
        console.error("Error fetching organizations: ", error);
      }
    };

    fetchOrganizations();
  }, [colRefOrganizations]);

  useEffect(() => {
    if (user) {
      const q = query(colRefProfile, where("email", "==", user.email));
      getDocs(q)
        .then((querySnapshot) => {
          if (!querySnapshot.empty) {
            const userData = querySnapshot.docs[0].data();
            setValues(userData);
          }
        })
        .catch((error) => {
          console.error("Error fetching user data: ", error);
        });
    }
  }, [user, colRefProfile]);

  const handleChangeText = (key, text) => {
    setValues((prevValues) => ({
      ...prevValues,
      [key]: text,
    }));
  };

  return (
    <View style={styles.container}>
      <Text style={styles.heading}>Edit Your Profile</Text>
      <TextInput
        name="firstName"
        style={styles.input}
        placeholder="First Name"
        value={values.firstName}
        onChangeText={(text) => handleChangeText("firstName", text)}
      />
      <TextInput
        name="lastName"
        style={styles.input}
        placeholder="Last Name"
        value={values.lastName}
        onChangeText={(text) => handleChangeText("lastName", text)}
      />
      <TextInput
        placeholder="Birthday"
        style={styles.input}
        value={values.birthday}
        onChangeText={(text) => handleChangeText("birthday", text)}
      />
      <TextInput
        name="phoneNumber"
        style={styles.input}
        placeholder="Phone Number"
        value={values.phoneNumber}
        onChangeText={(text) => handleChangeText("phoneNumber", text)}
      />
      <TextInput
        name="organization"
        style={styles.input}
        placeholder="Organization"
        value={values.weight}
        onChangeText={(text) => handleChangeText("organization", text)}
      />
       <Picker
        selectedValue={values.organization}
        onValueChange={(value) => handleChangeText("organization", value)}
        style={styles.input}
      >
        <Picker.Item label="Select Organization" value="" />
        {organizationList.map((org) => (
          <Picker.Item key={org.id} label={org.organizationName} value={org.id} />
        ))}
      </Picker>
       <TouchableOpacity
        style={styles.button}
        onPress={handleSaveProfile}
      >
        <Text style={styles.buttonText}>Save</Text>
      </TouchableOpacity>
      <TouchableOpacity
        style={styles.cancelButton}
        onPress={handleCancel}
      >
        <Text style={styles.buttonText}>Cancel</Text>
      </TouchableOpacity>
    </View>
  );
}
const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: "center",
    alignItems: "center",
    backgroundColor: "#f0f0f0",
    padding: 20,
  },
  heading: {
    fontSize: 24,
    fontWeight: "bold",
    marginBottom: 20,
    color: "#333",
  },
  input: {
    width: "80%",
    height: 40,
    borderColor: "#C5C2B7",
    borderWidth: 1,
    borderRadius: 10,
    padding: 10,
    marginBottom: 10,
  },

  button: {
    backgroundColor: "#38362d",
    justifyContent: "center",
    alignItems: "center",
    width: "30%",
    padding: 10,
    borderRadius: 5,
    marginTop: 10,
    backgroundColor: "#7DCEA0",
    borderColor: "#DAF7A6",
    borderWidth: 2,
  },
  cancelButton: {
    backgroundColor: "#808080",
    justifyContent: "center",
    alignItems: "center",
    width: "40%",
    padding: 10,
    borderRadius: 5,
    marginTop: 10,
    width: "30%",
  },
  buttonText: {
    color: "#fff",
    fontSize: 14,
    fontWeight: "bold",
  },
  datePicker: {
    width: "100%",
  },
});
