import React, { useEffect, useState } from "react";
import { View, Text } from "react-native";
import { Avatar } from "react-native-elements";
import { useNavigation } from "@react-navigation/native";
import { getFirestore, collection, doc, getDoc } from "firebase/firestore";
import { auth } from "../../firebase";

const WorkoutPage = ({ route }) => {
  const { workoutId } = route.params;
  const [workoutDetails, setWorkoutDetails] = useState({});
  const [error, setError] = useState(null);
  const navigation = useNavigation();

  useEffect(() => {
    const fetchWorkoutDetails = async () => {
      try {
        const firestore = getFirestore();
        const workoutDocRef = doc(firestore, "workoutPlan", workoutId);
        const workoutDoc = await getDoc(workoutDocRef);

        if (workoutDoc.exists()) {
          setWorkoutDetails(workoutDoc.data());
        } else {
          console.error("Workout document not found!");
          // Handle the case where the workout document is not found
          // Set an error state and handle it in the UI
          setError("Workout document not found!");
        }
      } catch (error) {
        console.error("Error getting workout details: ", error);
        // Set an error state and handle it in the UI
        setError("Error getting workout details");
      }
    };

    fetchWorkoutDetails();
  }, [workoutId, navigation]);

  if (error) {
    // Handle the error state, for example, navigate back to the profile screen
    return (
      <View>
        <Text>{error}</Text>
        {/* You can add a button or navigation logic to go back to the profile screen */}
      </View>
    );
  }

  return (
    <View>
      <View style={styles.header}>
        <Avatar
          rounded
          size={120}
          icon={{ name: "person", type: "material" }}
          avatarStyle={styles.avatarStyle}
          activeOpacity={0.7}
        />
        <Text style={styles.emailText}>{auth.currentUser?.email}</Text>
      </View>
      {/* Display the workout details using workoutDetails state */}
      {/* ... */}
    </View>
  );
};

const styles = {
    // Your styles here
  };


export default WorkoutPage;
