import React, { useState } from "react";
import {
  View,
  Text,
  TextInput,
  TouchableOpacity,
  StyleSheet,
} from "react-native";
import styles from "./styles/SignInScreen"; // Import the styles

function SignInScreen({ navigation }) {
  const [values, setValues] = useState({
    email: "",
    password: "",
  });
  const [loginStatus, setLoginStatus] = useState(null);

  const handleSubmit = () => {
    // Simulating an API call using setTimeout (Replace with your API logic)
    setTimeout(() => {
      // Replace this section with your actual API call using Axios in React Native
      if (
        values.email === "example@email.com" &&
        values.password === "password"
      ) {
        // API call success, perform navigation to Home screen
        setLoginStatus("Login Successful");
        navigation.navigate("Home");
      } else {
        // API call failure, handle error
        setLoginStatus("Email or password is incorrect");
      }
    }, 1000); // Simulating a delay

    // Actual API call should be made here using Axios or Fetch
    // The logic inside setTimeout is a placeholder for API call handling.
  };

  return (
    <View style={styles.loginSignup}>
      <View style={styles.form}>
        <Text style={styles.formText}>Sign In</Text>
        {loginStatus && <Text style={styles.error}>{loginStatus}</Text>}
        <View>
          <Text style={styles.formLabel}>Email</Text>
          <TextInput
            style={styles.formInput}
            placeholder="Enter Email"
            value={values.email}
            onChangeText={(text) => setValues({ ...values, email: text })}
          />
        </View>
        <View>
          <Text style={styles.formLabel}>Password</Text>
          <TextInput
            style={styles.formInput}
            placeholder="Enter Password"
            secureTextEntry={true}
            value={values.password}
            onChangeText={(text) => setValues({ ...values, password: text })}
          />
        </View>
        <TouchableOpacity onPress={handleSubmit} style={styles.formButton}>
          <Text style={styles.buttonText}>Sign In</Text>
        </TouchableOpacity>
        <Text>If you do not have an account, click the Sign Up button</Text>
        <TouchableOpacity
          onPress={() => navigation.navigate("Register")}
          style={styles.formButton}
        >
          <Text style={styles.buttonText}>Sign Up</Text>
        </TouchableOpacity>
      </View>
    </View>
  );
}

export default SignInScreen;
