// for verison 10.5.2
import { initializeApp } from "firebase/app";
import { getAuth } from "firebase/auth";
import { getDatabase } from "firebase/database";
import { getFirestore, collection, getDocs, addDoc } from "firebase/firestore";

// Your web app's Firebase configuration
const firebaseConfig = {
  apiKey: "AIzaSyDtkmG9Z7HWCfGHjM8omgVA_LCNemv5wmQ",
  authDomain: "infx490-d9876.firebaseapp.com",
  databaseURL: "https://infx490-d9876-default-rtdb.firebaseio.com",
  projectId: "infx490-d9876",
  storageBucket: "infx490-d9876.appspot.com",
  messagingSenderId: "602189178002",
  appId: "1:602189178002:web:5d56c140f3ef5a8acef24b",
  measurementId: "G-MNNEM90NZC",
};
// Initialize Firebase
const app = initializeApp(firebaseConfig);
const firestore = getFirestore(app); // Use getFirestore to get the Firestore instance

const auth = getAuth(app);
const db = getDatabase(app);

const unsubscribeAuthState = onAuthStateChanged(auth, (user) => {
  if (user) {
    userId = user.uid;

    // Create a reference to the 'signUp' collection
    const colUser = collection(firestore, 'signUp');

    // Create a query that filters documents based on the userId
    const userQuery = query(colUser, where('userId', '==', userId));

    // Execute the query
    getDocs(userQuery)
      .then((snapshot) => {
        snapshot.forEach((doc) => {
          // Access the userType from the document data
          const userType = doc.data().userType;
          console.log('UserType:', userType);

          // Now you can use userType within this scope or pass it to another function
          // For example, you can call a function passing userType as an argument
          someFunction(userType);
        });
      })
      .catch((error) => {
        console.error('Error getting documents: ', error);
      });
  }
});

// Define someFunction to use userType
function someFunction(userType) {
  // Do something with userType
  console.log('UserType in someFunction:', userType);
}

useEffect(() => {
  return () => unsubscribeAuthState();
}, []);



