import React, { useState } from "react";
import { Text, View, TextInput, TouchableOpacity } from "react-native";

const SignInScreen = ({ navigation }) => {
  const [values, setValues] = useState({
    email: "",
    password: "",
  });

  const [loginStatus, setLoginStatus] = useState(null);

  const handleSubmit = () => {
    fetch("http://localhost:8081/login", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(values),
    })
      .then((response) => response.json())
      .then((data) => {
        const token = data.token;
        AsyncStorage.setItem("token", token); // Storing the token securely
        console.log(data);
        setLoginStatus("Login Successful");
        // Navigate to Home screen
        navigation.navigate("Home");
      })
      .catch((error) => {
        console.error(error);
        setLoginStatus("Email or password is incorrect");
      });
  };

  return (
    <View>
      <View>
        <Text>Sign In</Text>
        {loginStatus && <Text style={styles.error}>{loginStatus}</Text>}
        <View>
          <Text>Email</Text>
          <TextInput
            style={styles.input}
            placeholder="Enter Email"
            value={values.email}
            onChangeText={(text) => setValues({ ...values, email: text })}
          />
        </View>
        <View>
          <Text>Password</Text>
          <TextInput
            style={styles.input}
            placeholder="Enter Password"
            secureTextEntry
            value={values.password}
            onChangeText={(text) => setValues({ ...values, password: text })}
          />
        </View>
        <TouchableOpacity onPress={handleSubmit} style={styles.button}>
          <Text>Sign In</Text>
        </TouchableOpacity>
        <Text>If you do not have an account, click Sign Up button</Text>
        <TouchableOpacity
          onPress={() => navigation.navigate("Register")}
          style={styles.button}
        >
          <Text>Sign Up</Text>
        </TouchableOpacity>
      </View>
    </View>
  );
};

export default SignInScreen;

const styles = {
  error: {
    color: "red",
  },
  input: {
    borderWidth: 1,
    borderColor: "black",
    padding: 10,
    marginBottom: 10,
  },
  button: {
    backgroundColor: "lightgreen",
    alignItems: "center",
    padding: 10,
    margin: 10,
  },
};
