import React, { useState, useEffect } from "react";
import { StyleSheet, Text, View, TextInput } from "react-native";
import { Button } from "react-native-elements";
import { getAuth, signInWithEmailAndPassword, onAuthStateChanged } from "firebase/auth";
import { getFirestore, collection, query, where, getDocs } from "firebase/firestore";
import app from "../../firebase"; 
import TabNavigator from "./TabNavigator"; // Update the import path based on your project structure

const auth = getAuth(app); // Initialize Firebase Authentication

const LoginScreen = ({ navigation }) => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [loading, setLoading] = useState(true);
  const [userType, setUserType] = useState(null); // Add userType state

  useEffect(() => {
    const fetchUserType = async () => {
      const unsubscribe = onAuthStateChanged(auth, (user) => {
        if (user) {
          const userId = user.uid;

          const colUser = collection(getFirestore(app), 'signUp');
          const userQuery = query(colUser, where('userId', '==', userId));

          getDocs(userQuery)
            .then((snapshot) => {
              snapshot.forEach((doc) => {
                const userType = doc.data().userType;
                console.log('UserType:', userType);
                setUserType(userType); // Set the userType state
              });
            })
            .catch((error) => {
              console.error('Error getting documents: ', error);
            });
        }
      });

      return unsubscribe;
    };

    fetchUserType();
  }, [navigation]);

  useEffect(() => {
    if (userType !== null) {
      setLoading(false);
    }
  }, [userType]);

  const navigateSignUp = () => {
    navigation.navigate("SignUpScreen");
  };

  const handleLogin = async () => {
    if (!email || !password) {
      alert("Please enter both email and password.");
      return;
    }

    try {
      await signInWithEmailAndPassword(auth, email, password);
      const user = auth.currentUser;
      console.log("Logged in with", user.email);
      navigation.navigate("HomeScreen");
    } catch (error) {
      alert(error.message);
    }

  };

  if (loading) {
    return (
      <View style={styles.container}>
        <Text>Loading...</Text>
      </View>
    );
  }

  // Get the currently authenticated user
  const auth = getAuth(app);
  let userId;

  const unsubscribeAuthState = onAuthStateChanged(auth, (user) => {
    if (user) {
      userId = user.uid;

      // Create a reference to the 'signUp' collection
      const colUser = collection(firestore, 'signUp');

      // Create a query that filters documents based on the userId
      const userQuery = query(colUser, where('userId', '==', userId));

      // Execute the query
      getDocs(userQuery)
        .then((snapshot) => {
          snapshot.forEach((doc) => {
            // Access the userType from the document data
            const userType = doc.data().userType;
            console.log('UserType:', userType);

            someFunction(userType);
          });
        })
        .catch((error) => {
          console.error('Error getting documents: ', error);
        });
    }
  });

  // Define someFunction to use userType
  function someFunction(userType) {
    // Do something with userType
    console.log('UserType in someFunction:', userType);
  }
  useEffect(() => {
    return () => unsubscribeAuthState();
  }, []);



  return (
    <View style={styles.container}>
      <View>
        <Text style={styles.greeting}>Welcome to Flexi Truck!</Text>
      </View>
      <View style={styles.inputContainer}>
        <TextInput
          placeholder="Email"
          style={styles.input}
          value={email}
          onChangeText={(text) => setEmail(text)}
        />
        <TextInput
          placeholder="Password"
          style={styles.input}
          secureTextEntry
          value={password}
          onChangeText={(text) => setPassword(text)}
        />
      </View>
      <View style={styles.buttonContainer}>
        <Button
          containerStyle={styles.button}
          buttonStyle={styles.button}
          onPress={handleLogin}
          title="Login"
          titleStyle={styles.buttonText}
        />
        <Button
          containerStyle={styles.button}
          buttonStyle={[styles.button, styles.buttonOutline]}
          onPress={navigateSignUp}
          title="Register"
          titleStyle={styles.buttonOutlineText}
        />
      </View>
      <TabNavigator userType={userType} /> {/* Pass userType as a prop */}
    </View>
  );
};
const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: "center",
    alignItems: "center",
    backgroundColor: "white",
  },
  greeting: {
    fontSize: 28,
    fontWeight: "bold",
  },
  inputContainer: {
    width: "70%",
    marginBottom: 20,
  },
  input: {
    backgroundColor: "white",
    paddingHorizontal: 15,
    paddingVertical: 10,
    borderRadius: 10,
    marginTop: 10,
    borderColor: "green",
    borderWidth: 1,
  },
  buttonContainer: {
    width: "30%",
    justifyContent: "center",
    alignItems: "center",
    marginTop: 40,
  },
  button: {
    width: "100%",
    borderRadius: 10,
  },
  buttonOutline: {
    backgroundColor: "white",
    marginTop: 5,
    borderColor: "#0782F9",
    borderWidth: 2,
  },
  buttonText: {
    fontWeight: "700",
    fontSize: 16,
  },
  buttonOutlineText: {
    color: "#0782F9",
    fontSize: 16,
    fontWeight: "700",
  },
  textContainer: {},
  text: {},
});

export default LoginScreen;
