import React, { useState } from "react";
import {
  View,
  Text,
  TextInput,
  TouchableOpacity,
  StyleSheet,
} from "react-native";

const SignInScreen = ({ navigation }) => {
  const [values, setValues] = useState({
    email: "",
    password: "",
  });

  const [loginStatus, setLoginStatus] = useState(null);

  const handleSignIn = () => {
    setTimeout(() => {
      if (
        values.email === "user@example.com" &&
        values.password === "password"
      ) {
        const token = "sampleToken";
        console.log("Login Successful");
        setLoginStatus("Login Successful");
        navigation.navigate("homescreen");
      } else {
        console.error("Email or password is incorrect");
        setLoginStatus("Email or password is incorrect");
      }
    }, 1000);
  };

  return (
    <View style={styles.loginSignup}>
      <View style={styles.form}>
        <Text style={styles.formH2}>Sign In</Text>
        {loginStatus && (
          <Text style={styles.successMessage}>{loginStatus}</Text>
        )}
        <TextInput
          placeholder="Enter Email"
          value={values.email}
          onChangeText={(text) => setValues({ ...values, email: text })}
          style={styles.formInput}
        />
        <TextInput
          placeholder="Enter Password"
          secureTextEntry={true}
          value={values.password}
          onChangeText={(text) => setValues({ ...values, password: text })}
          style={styles.formInput}
        />
        <TouchableOpacity onPress={handleSignIn} style={styles.formButton}>
          <Text>Sign In</Text>
        </TouchableOpacity>
        <Text>If you do not have an account, click Sign Up button</Text>
        <TouchableOpacity
          onPress={() => navigation.navigate("Register")}
          style={styles.formButton}
        >
          <Text style={styles.formLink}>Sign Up</Text>
        </TouchableOpacity>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  loginSignup: {
    flex: 1,
    justifyContent: "center",
    alignItems: "center",
    backgroundColor: "#f0f0f0",
    padding: 0,
    margin: 0,
  },
  form: {
    backgroundColor: "#ffffff",
    padding: 20,
    borderRadius: 10,
    width: "100%",
    shadowColor: "rgba(0, 0, 0, 0.1)",
    shadowOffset: { width: 0, height: 0 },
    shadowRadius: 10,
    textAlign: "center",
  },
  formH2: {
    fontWeight: "bold",
    color: "#333",
    marginBottom: 20,
  },
  formInput: {
    width: "100%",
    padding: 10,
    marginBottom: 15,
    borderWidth: 1,
    borderColor: "#ccc",
    borderRadius: 5,
    outline: "none",
  },
  formButton: {
    width: "100%",
    backgroundColor: "#DBB441",
    color: "#fff",
    padding: 12,
    borderRadius: 5,
    cursor: "pointer",
  },
  successMessage: {
    color: "#61924B",
    fontWeight: "bold",
  },
  formLink: {
    color: "whitesmoke",
    fontWeight: "bold",
  },
  // Add other styles as per your requirement
});

export default SignInScreen;
