import React, { useEffect, useState } from "react";
import { View, Text, StyleSheet } from "react-native";
import { Avatar } from "react-native-elements";
import { useNavigation } from "@react-navigation/native";
import { getFirestore, collection, doc, getDoc } from "firebase/firestore";
import { auth } from "../../firebase";

const WorkoutPage = ({ route }) => {
  const { workoutId } = route.params;
  const [workoutDetails, setWorkoutDetails] = useState({});
  const [error, setError] = useState(null);
  const navigation = useNavigation();

  useEffect(() => {
    const fetchWorkoutDetails = async () => {
      try {
        const firestore = getFirestore();
        const workoutDocRef = doc(firestore, "workoutPlan", workoutId);
        const workoutDoc = await getDoc(workoutDocRef);

        if (workoutDoc.exists()) {
          setWorkoutDetails(workoutDoc.data());
        } else {
          console.error("Workout document not found!");
          setError("Workout document not found!");
        }
      } catch (error) {
        console.error("Error getting workout details: ", error);
        setError("Error getting workout details");
      }
    };

    fetchWorkoutDetails();
  }, [workoutId, navigation]);

  if (error) {
    return (
      <View style={styles.container}>
        <Text>{error}</Text>
      </View>
    );
  }

  return (
    <View style={styles.container}>
      <View style={styles.header}>
        <Avatar
          rounded
          size={120}
          icon={{ name: "person", type: "material" }}
          avatarStyle={styles.avatarStyle}
          activeOpacity={0.7}
        />
        <Text style={styles.emailText}>{auth.currentUser?.email}</Text>
      </View>
      <View style={styles.tableContainer}>
        <Text style={styles.tableHeader}>{workoutDetails.workoutName}</Text>
        <View style={styles.tableRow}>
          <Text style={styles.tableCellHeader}>Exercise Name</Text>
          <Text style={styles.tableCellHeader}>Set</Text>
          <Text style={styles.tableCellHeader}>Rep</Text>
        </View>
        {workoutDetails.exercises.map((exercise, index) => (
          <View style={styles.tableRow} key={index}>
            <Text style={styles.tableCell}>{exercise.name}</Text>
            <Text style={styles.tableCell}>{exercise.set}</Text>
            <Text style={styles.tableCell}>{exercise.rep}</Text>
          </View>
        ))}
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: "center",
    backgroundColor: "#f0f0f0",
    paddingTop: 20,
  },
  header: {
    alignItems: "center",
    marginBottom: 20,
  },
  avatarStyle: {
    borderWidth: 2,
    borderColor: "#fff",
    backgroundColor: "#3498db",
  },
  emailText: {
    fontSize: 18,
    fontWeight: "bold",
    marginTop: 10,
  },
  tableContainer: {
    backgroundColor: "#fff",
    width: "80%",
    padding: 10,
    marginTop: 20,
  },
  tableHeader: {
    fontSize: 20,
    fontWeight: "bold",
    marginBottom: 10,
  },
  tableRow: {
    flexDirection: "row",
    borderBottomWidth: 1,
    borderColor: "#ccc",
    alignItems: "center",
  },
  tableCell: {
    flex: 1,
    padding: 10,
    fontSize: 14,
  },
  tableCellHeader: {
    flex: 1,
    padding: 10,
    fontSize: 16,
    fontWeight: "bold",
  },
});

export default WorkoutPage;
