import React from "react";
import { createBottomTabNavigator } from "@react-navigation/bottom-tabs";
import { createNativeStackNavigator } from "@react-navigation/native-stack";
import Ionicons from "react-native-vector-icons/Ionicons";
import DietScreen from "../screens/DietScreen";
import HomeScreen from "../screens/HomeScreen";
import ProfileScreen from "../screens/ProfileScreen";
import DoctorProfileScreen from "../screens/DoctorProfileScreen";
import { useFirestoreDocData, useFirestore } from "reactfire";

const Tab = createBottomTabNavigator();
const Stack = createNativeStackNavigator();

const HomeStackNavigator = ({ route }) => {
  const userType = route.params?.userType || 'default'; // Default to some value if not present

  return (
    <Stack.Navigator>
      <Stack.Screen
        name="HomeStack"
        component={HomeScreen}
        options={{ headerShown: false }}
      />
    </Stack.Navigator>
  );
};

const HomeTabNavigator = ({ route }) => {
  const userType = route.params?.userType || 'default'; // Default to some value if not present
  const firestore = useFirestore();

  return (
    <Tab.Navigator
      screenOptions={({ route }) => ({
        tabBarIcon: ({ focused, color, size }) => {
          let iconName;

          if (route.name === "Home") {
            iconName = "md-home-outline";
          } else if (route.name === "Diet") {
            iconName = "md-restaurant-outline";
          } else if (route.name === "Profile") {
            iconName = "md-person-outline";
          }

          return <Ionicons name={iconName} size={size} color={color} />;
        },
        tabBarActiveTintColor: "#216afc",
        tabBarInactiveTintColor: "gray",
        headerShown: false,
      })}
    >
      <Tab.Screen
        name="Home"
        component={HomeStackNavigator}
        initialParams={{ userType }}
        options={{ headerShown: false }}
      />

      <Tab.Screen name="Healthy Life" component={DietScreen} />

      {userType !== 'patient' && (
        <Tab.Screen
          name="DoctorProfile"
          component={DoctorProfileScreen}
          options={{
            headerShown: true,
            headerTitle: "Personal Information",
            headerTitleAlign: "center",
          }}
        />
      )}

      {userType !== 'doctor' && (
        <Tab.Screen
          name="Profile"
          component={ProfileScreen}
          options={{
            headerShown: true,
            headerTitle: "Personal Information",
            headerTitleAlign: "center",
          }}
        />
      )}
    </Tab.Navigator>
  );
};

export default HomeTabNavigator;
