import React, { useState } from "react";
import { View, TextInput, Text, StyleSheet, Alert } from "react-native";
import { Button } from "react-native-elements";
import { addDoc, collection } from "firebase/firestore";
import { firestore } from "../../firebase";
import { getAuth } from "firebase/auth";

const PatientFormScreen = ({ navigation, route }) => {
  const auth = getAuth();
  const user = auth.currentUser;
  const email = user.email;

  const [values, setValues] = useState({
    firstName: "",
    lastName: "",
    birthday: "",
    phoneNumber: "",
    weight: "",
    height: "",
    medicalCondition: "",
    email: email,
  });

  const handleSaveForm = async () => {
    // Validate input
    if (
      !values.firstName ||
      !values.lastName ||
      !values.birthday ||
      !values.phoneNumber ||
      !values.weight ||
      !values.height ||
      !values.medicalCondition
    ) {
      alert("Please fill out all the fields before submitting your registration.");
      return;
    }

    const colRefPatients = collection(firestore, "patients");

    try {
      await addDoc(colRefPatients, {
        ...values,
      });

      console.log("Patient data added successfully!");
      navigation.navigate("LoginScreen");
    } catch (error) {
      alert("Error adding patient data: " + error.message);
    }
  };
  return (
    <View style={styles.container}>
      <Text style={styles.headline}>Please fill out the form to complete your registration.</Text>

      <TextInput
        placeholder="First Name"
        style={styles.input}
        value={values.firstName}
        onChangeText={(text) => setValues({ ...values, firstName: text })}
      />
      <TextInput
        placeholder="Last Name"
        style={styles.input}
        value={values.lastName}
        onChangeText={(text) => setValues({ ...values, lastName: text })}
      />
      <TextInput
        placeholder="Birthday"
        style={styles.input}
        value={values.birthday}
        onChangeText={(text) => setValues({ ...values, birthday: text })}
      />
      <TextInput
        placeholder="Phone Number"
        style={styles.input}
        value={values.phoneNumber}
        onChangeText={(text) => setValues({ ...values, phoneNumber: text })}
      />
      <TextInput
        placeholder="Weight"
        style={styles.input}
        value={values.weight}
        onChangeText={(text) => setValues({ ...values, weight: text })}
      />
      <TextInput
        placeholder="Height"
        style={styles.input}
        value={values.height}
        onChangeText={(text) => setValues({ ...values, height: text })}
      />
      <TextInput
        placeholder="Medical Condition"
        style={styles.input}
        value={values.medicalCondition}
        onChangeText={(text) =>
          setValues({ ...values, medicalCondition: text })
        }
      />

      <Button
        containerStyle={styles.button}
        buttonStyle={[styles.button, styles.buttonOutline]}
        onPress={handleSaveForm}
        title="Submit Registration"
        titleStyle={styles.buttonOutlineText}
      />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: "center",
    alignItems: "center",
    backgroundColor: "white",
  },
  headline: {
    fontSize: 18,
    fontWeight: "bold",
    marginBottom: 20,
  },
  input: {
    backgroundColor: "white",
    paddingHorizontal: 15,
    paddingVertical: 10,
    borderRadius: 10,
    marginTop: 10,
    borderColor: "green",
    borderWidth: 1,
    width: "80%", // Adjust the width as needed
  },
  button: {
    width: "50%",
    marginTop: 20,
    borderRadius: 10,
    marginLeft: "15%",
  },
  buttonOutline: {
    backgroundColor: "#7DCEA0",
    marginTop: 5,
    borderColor: "#DAF7A6",
    borderWidth: 2,
  },
  buttonOutlineText: {
    color: "white",
    fontSize: 16,
    fontWeight: "700",
  },
});

export default PatientFormScreen;
